package in.folknet.folkbloreattendance;import android.content.Intent;import android.os.Bundle;import android.view.View;import android.widget.ImageButton;import androidx.appcompat.app.AppCompatActivity;public class InternetConnection extends AppCompatActivity {ImageButton refresh;    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_internet_connection);        refresh = (ImageButton)findViewById(R.id.refresh);        refresh.setOnClickListener(new View.OnClickListener(){            @Override            public void onClick(View v) {                Intent intent = new Intent(InternetConnection.this, PreFetchActivity.class);                startActivity(intent);            }});    }}