package in.folknet.folkbloreattendance;import android.content.Intent;import android.os.Bundle;import android.util.Log;import android.view.View;import android.widget.Button;import android.widget.Toast;import androidx.appcompat.app.AppCompatActivity;public class Main2Activity extends AppCompatActivity {    Button b1,b2;    String zone = "", program = "", category = "", location = "", session = "";    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_main2);        Log.d("Test", "Line 87");        Log.d("FOLK", "onClick: Main Activity 2");        b1=(Button)findViewById(R.id.b1);        b2=(Button)findViewById(R.id.b2);        zone = getIntent().getStringExtra("Zone");        program = getIntent().getStringExtra("Program");        category = getIntent().getStringExtra("Category");        location = getIntent().getStringExtra("Location");        session = getIntent().getStringExtra("Session");        Toast.makeText(this, "Zone: " + zone + "\nProgram: " + program +                "\nCategory: " + category + "\nLocation: " + location + "\nSession: " + session,                Toast.LENGTH_SHORT).show();        b1.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View view) {                Intent intent = new Intent(getApplicationContext(), FOLK.class);                startActivity(intent);            }        });        b2.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View view) {                Intent intent = new Intent(getApplicationContext(), FOLK4.class);                startActivity(intent);            }        });    }}